foaf:Person.sql:sqlx:
select xmlelement ('http://xmlns.com/foaf/0.1/:knows', xmlelement ('http://xmlns.com/foaf/0.1/:Person', xmlattributes ('http://'|| WA_CNAME ()|| '/dataspace/' || u_name || '#person' as 'http://www.w3.org/1999/02/22-rdf-syntax-ns#:about'), either ( length (u_full_name) , xmlelement ('http://xmlns.com/foaf/0.1/:name', wa_utf8_to_wide (u_full_name)), null), xmlelement ('http://xmlns.com/foaf/0.1/:nick', wa_utf8_to_wide (u_name)),
xmlelement ('http://www.w3.org/2000/01/rdf-schema#:seeAlso', xmlattributes ( 'http://'|| WA_CNAME ()|| '/dataspace/person/' || u_name || '/foaf.rdf' as 'http://www.w3.org/1999/02/22-rdf-syntax-ns#:resource'))
)) from ( select u_id,u_full_name,u_name from sn_person, sn_related, sys_users where sne_org_id = u_id and sne_id = snr_to and snr_from = :sne union select u_id,u_full_name,u_name from sn_person, sn_related, sys_users where sne_org_id = u_id and sne_id = snr_from and snr_to = :sne ) sub
