sect1.programlisting:
CREATE PROCEDURE RES (IN I INTEGER)
{
    DECLARE STR, STR2 VARCHAR;

    RESULT_NAMES (I, STR);
    RESULT (I, 'FIRST');
    RESULT (I + 1, 'SECOND');
    RESULT_NAMES (I, STR, STR2);

    END_RESULT ();

    RESULT (I, 'FIRST');
    RESULT (I + 1, 'SECOND');
    RESULT (I + 2, 'THIRD', 'FF');
    RESULT (I + 3, 'FOURTH', 'FF');

    RETURN 1;
}

sect2.programlisting:
CREATE PROCEDURE NAME (parameter , parameter...) { statement ... }
parameter: parameter_type name data_type
parameter_type: IN | OUT | INOUT

sect2.screen:
CREATE PROCEDURE FIBO (IN X INTEGER)
{
    IF (X < 2)
	RETURN X;
    ELSE
	RETURN (FIBO (X - 1) + FIBO (X - 2));
}

CREATE PROCEDURE CFIBO (IN X INTEGER)
{
    DECLARE RES INTEGER;

    RES := FIBO (X);

    RESULT_NAMES (RES);

    RESULT (RES);
}

IF (A > B)
    A := A + 1;
ELSE
    B := B + 1;

WHILE (1 = 1) {
    A := A + 1;
}

sect2.programlisting:
compound_statement : '{' statement_list '}' ;
statement_list : statement_in_cs | statement_list statement_in_cs ;
statement_in_cs : local_declaration ';' | compound_statement | routine_statement ';' | control_statement | label ':' statement ;
statement : routine_statement ';' | control_statement | compound_statement ;
local_declaration : cursor_def | variable_declaration | handler_declaration ;
variable_declaration : DECLARE variable_list data_type ;
variable_list : NAME | variable_list ',' NAME ;

goto_statement : GOTO label ;
label : NAME
return_statement : RETURN scalar_exp | RETURN ;

sect2.screen:
CREATE PROCEDURE COUNT_CUSTOMERS (IN C_NAME VARCHAR)
{
    DECLARE COUNT INTEGER;

    COUNT := 0;

    DECLARE C CURSOR FOR SELECT C_ID FROM CUSTOMER WHERE C_NAME = C_NAME;

    WHENEVER SQLSTATE '4001' GOTO DEADLOCK;

    WHENEVER NOT FOUND GOTO DONE;

    OPEN C;
    WHILE (1=1)
    {
	FETCH C INTO N;
	COUNT := COUNT + 1;
    }

DONE:
    RETURN COUNT;

DEADLOCK:
    RETURN -1;
}

sect2.programlisting:
function_call : NAME '(' opt_scalar_exp_commalist ')' | call '(' scalar_exp ')' '('opt_scalar_exp_commalist ')' ;
call_statement : CALL NAME '(' opt_scalar_exp_commalist ')' | function_call ;
assignment_statement : lvalue EQUALS scalar_exp | lvalue '=' scalar_exp ;
lvalue : NAME

sect2.screen:
CREATE PROCEDURE COMPUTED_CALL (IN Q INTEGER)
{
    DECLARE FN VARCHAR;

    FN := 'F';

    --- CALL FUNCTION FF WITH ARGUMENT 11.
    R := CALL (CONCATENATE (FN, 'F')) (11);
}

sect2.programlisting:
SELECT opt_all_distinct selection INTO target_commalist table_exp with_opt_cursor_options_list ;
opt_all_distinct : /* empty */ | ALL | DISTINCT ;
with_opt_cursor_options_list : /* empty */ | WITH opt_cursor_options_list ;
cursor_option : EXCLUSIVE | PREFETCH INTNUM ;
cursor_options_commalist : cursor_option | cursor_options_commalist ',' cursor_option ;
opt_cursor_options_list : /* empty */ | '(' cursor_options_commalist ')' ;
open_statement : OPEN cursor opt_cursor_options_list ;
fetch_statement : FETCH cursor INTO target_commalist ;
target_commalist : variable | target_commalist ',' variable ;
close_statement : CLOSE cursor ;

sect2.screen:
The statements:

{
    DECLARE CR CURSOR FOR SELECT C_NAME FROM CUSTOMER WHERE C_ID = ID;
    OPEN CR;
    FETCH CR INTO NAME;
    CLOSE CR;
}

and

SELECT C_NAME INTO NAME FROM CUSTOMER WHERE C_ID = ID;

have the same effect.

CREATE PROCEDURE WITHDRAW (IN C_ID VARCHAR, IN DELTA NUMERIC)
{
    DECLARE BAL NUMERIC;

    DECLARE CR CURSOR FOR SELECT C_BALANCE FROM CUSTOMER WHERE C_ID = C_ID;

    WHENEVER NOT FOUND GOTO NOCUSTOMER;

    OPEN CR (EXCLUSIVE);
    FETCH CR INTO BAL;
    IF (BAL > DELTA)
	UPDATE CUSTOMER SET C_BALANCE = BAL - DELTA WHERE CURRENT OF CR;
    ELSE
	SIGNAL ('NOMONEY', 'INSUFFICIENT BALANCE.');

    RETURN;

NOCUSTOMER:
    SIGNAL ('NOCUS', 'BAD CUSTOMER ID');
}

sect2.programlisting:
trigger_def : CREATE TRIGGER NAME action_time event ON q_table_name opt_order opt_old_ref trig_action ;
opt_order : /* empty */ | ORDER INTNUM ;
trig_action : compound_statement ;
action_time : BEFORE | AFTER ;
event : INSERT | UPDATE opt_column_commalist | DELETE ;
opt_old_ref : /* empty */ | REFERENCING old_commalist ;
old_commalist : old_alias | old_commalist ',' old_alias ;
old_alias : OLD AS NAME | NEW AS NAME ;
drop_trigger : DROP TRIGGER q_table_name

