refsect1.programlisting:
-- Target site definition create table WS.WS.VFS_SITE ( VS_DESCR varchar, -- human readable description VS_HOST varchar, -- target host name VS_URL varchar, -- start URL path VS_INX varchar (5),-- reserved VS_OWN integer, -- local WebDAV owner of the retrieved content VS_ROOT varchar, -- local WebDAV collection destination VS_NEWER datetime, -- update only newer than (this date) VS_DEL varchar, -- delete local resource if removal on target detected VS_FOLLOW varchar, -- walk on this links VS_NFOLLOW varchar, -- do not walk on this links VS_SRC varchar, -- retrieve images VS_OPTIONS varchar, -- if target wants authentication the uid/pwd are stored VS_METHOD varchar, -- use WebDAV or traditional HTTP method VS_OTHER varchar, -- walk on foreign links primary key (VS_HOST, VS_ROOT)); create index VS_HOST_ROOT on WS.WS.VFS_SITE (VS_HOST, VS_URL, VS_ROOT) ;
-- Queue table create table WS.WS.VFS_QUEUE ( VQ_HOST varchar, -- target host name VQ_TS datetime, -- date and time of adding into the queue VQ_URL varchar, -- target URL path VQ_ROOT varchar, -- local WebDAV folder destination VQ_STAT varchar (15), -- status VQ_OTHER varchar, -- retrieved from other site primary key (VQ_HOST, VQ_URL, VQ_ROOT)); create index VQ_HOST_ROOT_STAT on WS.WS.VFS_QUEUE (VQ_HOST, VQ_ROOT, VQ_STAT); create index VQ_HOST_ROOT on WS.WS.VFS_QUEUE (VQ_HOST, VQ_ROOT); create index VQ_HOST_TIME on WS.WS.VFS_QUEUE (VQ_HOST, VQ_ROOT, VQ_TS); create index VQ_TS on VFS_QUEUE (VQ_TS) ;
-- Retrieved URLs table create table WS.WS.VFS_URL ( VU_HOST varchar, -- target host name VU_URL varchar, -- retrieved URL path VU_ROOT varchar, -- local WebDAV folder destination VU_CHKSUM varchar, -- content checksum VU_ETAG varchar, -- target's ETag VU_CPTIME datetime, -- retrieval date and time VU_OTHER varchar, -- retrieved from other site primary key (VU_HOST, VU_URL, VU_ROOT)); create index VU_HOST_ROOT on WS.WS.VFS_URL (VU_HOST, VU_ROOT) ;

