CREATE TABLE Xml_tab ( xmlval XMLType);
INSERT INTO Xml_tab VALUES ( xmltype('<?xml version="1.0"?> <EMP> <EMPNO>221</EMPNO> <ENAME>John</ENAME> </EMP>'));
INSERT INTO Xml_tab VALUES ( xmltype('<?xml version="1.0"?> <PO> <PONO>331</PONO> <PONAME>PO_1</PONAME> </PO>'));
-- now extract the numerical values for the employee numbers
SELECT e.xmlval.extract('//EMPNO/text()').getNumVal() as empno FROM Xml_tab WHERE e.xmlval.existsnode('/EMP/EMPNO') = 1;
