example.programlisting:
CREATE PROCEDURE all_tokens(IN pattern VARCHAR, IN str VARCHAR)
{
	DECLARE wrd VARCHAR;
	DECLARE ans VARCHAR;
	DECLARE str_i VARCHAR;

	ans:='';
	str_i := str;
	wrd := regexp_match(pattern,str_i);
	WHILE (wrd IS NOT NULL)
	{
		ans := concat(ans,',',wrd);
		wrd := regexp_match(pattern, str_i, 1);
	};
	RETURN ans;
};
