example.programlisting:
for select RES_CONTENT from WS.WS.SYS_DAV_RES 
 where contains (RES_CONTENT, 'place and knowledge')
do {
  http_value (search_excerpt (vector ('place', 'knowledge'), 
                              cast (RES_CONTENT as varchar), 
			      200000, 90, 200, 'b', 1),
	      'P');
}
