example.programlisting:
SQL>create procedure csr_demo() { declare kname, cvalue varchar; declare _key any; declare _output int;
xenc_key_RSA_create ('RSAKey1', 1024);
_key := xenc_x509_csr_generate ('RSAKey1', vector ( 'CN', 'Demo user', 'C', 'US', 'O', 'OpenLink', 'OU', 'Accounts', 'emailAddress', 'demo@openlinksw.com'), vector ('subjectAltName', 'URI: http://www.openlinksw.com/dataspace/person/demo#this', 'nsComment', 'Virtuoso Generated Certificate', 'authorityKeyIdentifier', 'keyid,issuer:always'));
xenc_x509_from_csr ('id_rsa', 'MyKey1', _key , sequence_next ('ca_id_rsa'), 365, 100); return xenc_pem_export ('MyKey1');
} ;
Done. -- 0 msec.
