DB.DBA.URLREWRITE_CREATE_REGEX_RULE ( 'tut_th_rule1', 1, '([^#]*)', vector('path'), 1, '/about/html/http/^{URIQADefaultHost}^%s%%01this', vector('path'), null, '(text/html)|(\\*/\\*)', 0, 303 );
DB.DBA.URLREWRITE_CREATE_REGEX_RULE ( 'tut_th_rule2', 1, '([^#]*)', vector('path'), 1, '/sparql?query=CONSTRUCT+{+%%3Chttp%%3A//^{URIQADefaultHost}^%U%%23this%%3E+%%3Fp+%%3Fo+}+FROM+%%3Chttp%%3A//^{URIQADefaultHost}^/thalia%%3E+WHERE+{+%%3Chttp%%3A//^{URIQADefaultHost}^%U%%23this%%3E+%%3Fp+%%3Fo+}&format=%U', vector('path', 'path', '*accept*'), null, '(text/rdf.n3)|(application/rdf.xml)', 0, null );
DB.DBA.URLREWRITE_CREATE_REGEX_RULE ( 'tut_th_rule3', 1, '(/[^#]*)/\x24', vector('path'), 1, '%U', vector('path'), null, null, 0, null );
create procedure DB.DBA.REMOVE_THALIA_RDF_DET() { declare colid int; colid := DAV_SEARCH_ID('/DAV/Thalia', 'C'); if (colid < 0) return; update WS.WS.SYS_DAV_COL set COL_DET=null where COL_ID = colid; } ;
DB.DBA.REMOVE_THALIA_RDF_DET();
drop procedure DB.DBA.REMOVE_THALIA_RDF_DET;
create procedure DB.DBA.THALIA_MAKE_RDF_DET() { declare uriqa_str varchar; uriqa_str := cfg_item_value(virtuoso_ini_path(), 'URIQA','DefaultHost'); uriqa_str := 'http://' || uriqa_str || '/thalia'; DB.DBA."RDFData_MAKE_DET_COL" ('/DAV/Thalia/RDFData/', uriqa_str, NULL); VHOST_REMOVE (lpath=>'/thalia/data/rdf'); DB.DBA.VHOST_DEFINE (lpath=>'/thalia/data/rdf', ppath=>'/DAV/Thalia/RDFData/All/', is_dav=>1, vsp_user=>'dba'); } ;
DB.DBA.THALIA_MAKE_RDF_DET();
drop procedure DB.DBA.THALIA_MAKE_RDF_DET;
-- procedure to convert path to DET resource name create procedure DB.DBA.THALIA_DET_REF (in par varchar, in fmt varchar, in val varchar) { declare res, iri any; declare uriqa_str varchar; uriqa_str := cfg_item_value(virtuoso_ini_path(), 'URIQA','DefaultHost'); uriqa_str := 'http://' || uriqa_str || '/thalia'; iri := uriqa_str || replace(val, '/', '_'); res := sprintf ('iid (%d).rdf', iri_id_num (iri_to_id (iri))); return sprintf (fmt, res); } ;
DB.DBA.URLREWRITE_CREATE_REGEX_RULE ('tut_th_rdf', 1, '/thalia/(.*)', vector('path'), 1, '/thalia/data/rdf/%U', vector('path'), 'DB.DBA.THALIA_DET_REF', 'application/rdf.xml', 2, 303);
DB.DBA.URLREWRITE_CREATE_RULELIST ( 'tut_th_rule_list1', 1, vector ( 'tut_th_rule1', 'tut_th_rule2', 'tut_th_rule3', 'tut_th_rdf' ));
DB.DBA.VHOST_REMOVE (lpath=>'/thalia'); DB.DBA.VHOST_DEFINE (lpath=>'/thalia', ppath=>'/DAV/Thalia/', vsp_user=>'dba', is_dav=>1, is_brws=>0, opts=>vector ('url_rewrite', 'tut_th_rule_list1'));
create procedure DB.DBA.LOAD_THALIA_ONTOLOGY_FROM_DAV() { declare content, urihost varchar; select cast (RES_CONTENT as varchar) into content from WS.WS.SYS_DAV_RES where RES_FULL_PATH = '/DAV/Thalia/thalia.owl'; DB.DBA.RDF_LOAD_RDFXML (content, 'http://demo.openlinksw.com/schemas/thalia#', 'http://demo.openlinksw.com/schemas/ThaliaOntology/1.0/'); urihost := cfg_item_value(virtuoso_ini_path(), 'URIQA','DefaultHost'); if (urihost = 'demo.openlinksw.com') { DB.DBA.VHOST_REMOVE (lpath=>'/schemas/thalia'); DB.DBA.VHOST_DEFINE (lpath=>'/schemas/thalia', ppath=>'/DAV/Thalia/thalia.owl', vsp_user=>'dba', is_dav=>1, is_brws=>0); } } ; DB.DBA.LOAD_THALIA_ONTOLOGY_FROM_DAV() ; drop procedure DB.DBA.LOAD_THALIA_ONTOLOGY_FROM_DAV ;
DB.DBA.XML_SET_NS_DECL ('thalia', 'http://demo.openlinksw.com/schemas/thalia#', 2) ;
