sect2.programlisting:
create procedure dt (in tn varchar)
{
	declare index_name, col, dtp varchar;
	declare t, nth integer;

	dtp := '-';

	result_names (col, t);	  --- first result set

	declare cr cursor for select COLUMN, COL_DTP
	  from SYS_COLS where TABLE = tn;

	whenever not found goto done;
	open cr;
	while (1=1) {
		fetch cr into col, t;
		result (col, t);
	}
done:
	result_names (index_name, nth, col);	 -- 2nd result set
	end_result ();

	declare icr cursor for select KEY_NAME, KP_NTH, COLUMN
	  from SYS_KEYS, SYS_KEY_PARTS, SYS_COLS
	  where KEY_TABLE = tn and KP_KEY_ID = KEY_ID
	  and KP_NTH < KEY_N_SIGNIFICANT and	 COL_ID = KP_COL;

	whenever not found goto k_done;
	open icr;
	while (1=1) {
		fetch icr into index_name, nth, col;
		result (index_name, nth, col);
	}
k_done:
	return 0;
}

-- WebDAV Users create view WS.WS.SYS_DAV_USER (U_ID, U_NAME, U_FULL_NAME, U_E_MAIL, U_PWD, U_GROUP, U_LOGIN_TIME, U_ACCOUNT_DISABLED, U_METHODS, U_DEF_PERMS, U_HOME) as select U_ID, U_NAME, U_FULL_NAME, U_E_MAIL, U_PASSWORD as U_PWD, U_GROUP, U_LOGIN_TIME, U_ACCOUNT_DISABLED, U_METHODS, U_DEF_PERMS, U_HOME from DB.DBA.SYS_USERS where U_IS_ROLE = 0 and U_DAV_ENABLE = 1;
-- WebDAV Groups create view WS.WS.SYS_DAV_GROUP (G_ID, G_NAME) as select U_ID as G_ID, U_NAME as G_NAME from DB.DBA.SYS_USERS where U_IS_ROLE = 1 and U_DAV_ENABLE = 1;
-- The granted groups to the WebDAV user create view WS.WS.SYS_DAV_USER_GROUP (UG_UID, UG_GID) as select GI_SUPER, GI_SUB from DB.DBA.SYS_ROLE_GRANTS where GI_DIRECT = 1;
create view SYS_USER_GROUP (UG_UID, UG_GID) as select GI_SUPER, GI_SUB from SYS_ROLE_GRANTS where GI_DIRECT = 1;

CREATE TABLE SYS_CHARSETS (
    CS_NAME varchar,			-- The "preferred" charset name
    CS_TABLE long nvarchar,		-- the mapping table of length 255 Wide chars
    CS_ALIASES long varchar		-- serialized vector of aliases
);

- - Message queue create table MAIL_MESSAGE_QUEUE ( MQ_ID varchar, - - FK MAIL_MESSAGE (MM_ID) MQ_RECIEVER varchar, - - foreign mail exchanger primary key (MQ_ID)) ;
create index MAIL_MSG_Q_REC on MAIL_MESSAGE_QUEUE (MQ_RECIEVER) ;
-- Mail Table create table DB.DBA.MAIL_MESSAGE ( MM_ID integer, -- Unique id of message (per user) MM_OWN varchar (128), -- Local WebDAV account (recipient, mail box owner) name, FK references WS.WS.SYS_DAV_USER (U_NAME) MM_FLD varchar (128), -- Message Folder (initial 'Inbox') MM_FROM varchar (512), -- From: RFC822 header field MM_TO varchar (512), -- To: RFC822 header field MM_CC varchar (512), -- Cc: RFC822 header field MM_BCC varchar (512), -- Bcc: RFC822 header field MM_SUBJ varchar (512), -- Subject of the message MM_REC_DATE varchar (50), -- Date of arrival MM_SND_TIME varchar (50), -- Date of posting MM_IS_READED integer, -- Read flag (0/1 not read, 1 - read) MM_BODY long varchar, -- Message content (including message header) primary key (MM_OWN, MM_FLD, MM_ID) ) ;

-- Temporary message MIME parts table (for message composition) create table DB.DBA.MAIL_PARTS ( MP_ID integer, -- Unique id per user (order of parts) MP_PART long varbinary, -- Message MIME part body MP_ATTRS long varbinary, -- Message MIME part attributes MP_OWN varchar (128), -- WebDAV user name - FK REFERENCES WS.WS.SYS_DAV_USER (U_NAME) primary key (MP_OWN, MP_ID) ) ;
- - Local mail users (this table will be needed only in case of different DB, DAV and MAIL users) create table MAIL_USERS ( MU_NAME varchar, MU_PWD varchar, primary key (MU_NAME)) ;
- - Allowed relay domains (only in case of allowed relaying) create table MAIL_RELAY ( MR_ALLOWED varchar; primary key (MR_ALLOWED)) ;
