class.screen:
-- Button class, encapsulate all controls originating a event create type vspx_button under vspx_field as ( bt_pressed int default 0, -- internal use only bt_style varchar default 'submit', -- button style bt_close_img varchar, -- this and below are alternate image links bt_open_img varchar, -- for making a two-state image buttons bt_url varchar -- if a url specified to button, this alter ) temporary self as ref -- it to a image link overriding method vc_render () returns any, constructor method vspx_button (name varchar, parent vspx_control), constructor method vspx_button (name varchar, label varchar, parent vspx_control) ;
-- this represents a browse button create type vspx_browse_button under vspx_button as ( vcb_selector varchar, -- url to be open in pop-up vcb_chil_options varchar default '', -- child window options vcb_fields any, -- fileds to be send as parameters vcb_browser_mode varchar default 'RES', -- COL, RES, STANDALONE vcb_list_mode integer default '1', -- 1 full list, 0 short list vcb_system varchar default '', -- 'dav', 'file', '' (none) vcb_xfer varchar default 'DOM', -- for now only this option is used vcb_current integer default 0, -- current dav collection id vcb_filter varchar default '*' -- filter for file list ) self as ref temporary constructor method vspx_browse_button (name varchar, parent vspx_control), overriding method vc_render () returns any ;
-- this represents a delete button create type vspx_delete_button under vspx_button as ( btd_table varchar, -- table for delete btd_key varchar -- key column for delete ) self as ref temporary constructor method vspx_delete_button (name varchar, parent vspx_control), constructor method vspx_delete_button (name varchar, label varchar, parent vspx_control) ;
-- these are representation of various button types create type vspx_radio_button under vspx_button self as ref temporary constructor method vspx_radio_button (name varchar, parent vspx_control), overriding method vc_render () returns any ; create type vspx_submit under vspx_button temporary self as ref constructor method vspx_submit (name varchar, parent vspx_control) ; create type vspx_logout_button under vspx_button self as ref temporary constructor method vspx_logout_button (name varchar, parent vspx_control), constructor method vspx_logout_button (name varchar, label varchar, parent vspx_control, page vspx_page) ;
create type vspx_return_button under vspx_button self as ref temporary overriding method vc_render () returns any, constructor method vspx_return_button (name varchar, parent vspx_control), constructor method vspx_return_button (name varchar, label varchar, int_name varchar, parent vspx_control) ;
